import React, { useEffect, useState } from 'react'
import { Accordion, Badge, Button, Card } from "react-bootstrap";
import MainScreen from "../../components/MainScreen";
import { Link, useSearchParams } from "react-router-dom";
import notes from '../../data/notes'
import axios from "axios"



const MyNotes = () => {
  const deleteHandler = (id)=>{
    if(window.confirm("Are you sure")){
      //delete
    }
  }

  const [notes,setNotes] = useState([]);

  const fetchNotes = async()=>{
    const { data } = await axios.get("/api/notes");
    setNotes(data);
    // console.log(data);
  }
  

  useEffect(() => {
    fetchNotes();
    console.log(notes);
  }, [])
  return (
    
    <MainScreen title='welcome back Shashank ...'>
        <Link to='createnote'>
          <Button style={{marginLeft:10,marginBottom:6}} size="lg" > 
            Create New Note
          </Button>
        </Link>
          {
            notes.map(note=>(
              <Accordion>
              <Card style={{margin: 10}}>
              
              <Card.Header style={{display: "flex" }}>
                <span style={{color: "black" , textDecoration: "none" , flex:1, cursor : "pointer", alignSelf :"center", fontSize: 18,
                }}>
                  <Accordion.Header as={Card.Text} variant='link' eventKey="0" style={{border:"none"}}>
                    {note.title}
                  </Accordion.Header>
                </span>
              <div>
                <Button href={`/note/${note._id}`}>Edit</Button>
                <Button variant='danger' className='mx-2'
                onClick={()=>deleteHandler(note._id)}
                >delete</Button>
              </div>
              </Card.Header>
            <Accordion.Body eventKey="0">
              <Card.Body>

                <h4>
                <Badge bg="success">{note.category}</Badge>
                  
                </h4>
                <blockquote className="blockquote mb-0">
                  <p>
                    {note.content}
                  </p>
                  <footer className="blockquote-footer">
                    Created on -date
                  </footer>
                </blockquote>
              </Card.Body>
            </Accordion.Body>
          </Card>
          </Accordion>
            ))
          }
    </MainScreen>
    
  )
}

export default MyNotes
